function updateTable(t,n=0){let a=document.getElementsByClassName("content__table")[0];n%3==0&&a.querySelectorAll(".content__table-row").forEach(t=>t.remove()),t.forEach((t,e)=>{addNewRow(a,t,e,n)})}function addNewRow(t,e,n,a){let o=document.createElement("div");for(var d in o.classList.add("content__table-row"),e){let t=document.createElement("div");t.innerHTML=e[d],t.classList.add("content__table-item"),o.appendChild(t)}let c=document.createElement("div");c.innerHTML=10*a+n+1,c.classList.add("content__table-item-id"),o.appendChild(c),t.appendChild(o)}async function postData(t="",e={}){const n=await fetch(t,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)});return n.json()}const button=document.querySelector(".button__load");button.addEventListener("click",t=>{postData("server_test.php",{page:button.getAttribute("data-page")}).then(t=>{var e=parseInt(button.getAttribute("data-page"));updateTable(t,e),button.setAttribute("data-page",e+1)})}),document.addEventListener("DOMContentLoaded",t=>{document.querySelector(".button__load").click(),document.querySelector(".nav__toggle").addEventListener("click",()=>{document.querySelector(".nav__list").classList.toggle("active")})});